import './App.css';
import { React } from 'react'
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom'
import { GlobalStoreContext } from '../store'
import { Banner, ListSelector, PlaylistCards, DeleteListModal, DeleteSongModal, Statusbar, EditSongModal} from './components'
/*
    This is our application's top-level component.
    
    @author McKilla Gorilla
*/
const App = () => {
    const { store } = useContext(GlobalStoreContext);
    let called = false;
    function start()
    {
        let callUndo = () => {
            store.undo();
        };

        let callRedo = () => {
            store.redo();
        };

        if(!store.called){
            store.called = true;
            document.addEventListener('keydown', function(event){
                if((event.key === 'z' || event.key === 'Z') && event.ctrlKey)
                {
                    callUndo();
                }
                else if((event.key === 'y' || event.key === 'Y') && event.ctrlKey)
                {
                    callRedo();
                }
            } );
        }
        let canAddSong = this.state.currentList !== null;
        let canUndo = this.tps.hasTransactionToUndo() && this.state.currentList !== null;
        let canRedo = this.tps.hasTransactionToRedo() && this.state.currentList !== null;
        let canClose = this.state.currentList !== null;
    }
    return (
        <Router>
            <Banner />
            <Switch>
                <Route path="/" exact component={ListSelector} />
                <Route path="/playlist/:id" exact component={PlaylistCards} />
                {start()}
            </Switch>

            <Statusbar />
            <DeleteListModal id = "deleteModal"/>
            <DeleteSongModal id = "deleteModal"/>
            <EditSongModal id = "deleteModal"/>
        </Router>
    )
} 

export default App
